/**
 * HackerRank - bigSorting
 * Consider an array of numeric strings where 
 * each string is a positive number with anywhere from 1 to 10^6 
 * digits. 
 * Sort the array's elements in non-decreasing, 
 * or ascending order of their integer values and 
 * return the sorted array.
 */


 function bigSorting(unsorted) {
    var BigNumber = require('bignumber.js');
    for(let i = 0;i < unsorted.length ; i++){
        let big = new BigNumber(unsorted[i]);
        unsorted[i] = big;
    }
    unsorted.sort((a,b)=>a.minus(b));
    
    for(let i = 0; i < unsorted.length; i++){
        unsorted[i] = unsorted[i].toFixed();
    }
    return unsorted;
}

console.log(bigSorting(['31415926535897932384626433832795','1','3','10','3','5']));
console.log(bigSorting(['','']));
